newdata2 <- data.frame( FS=as.numeric(d12$FS), QD=as.numeric(d12$QD), MQranksum=as.numeric(d12$MQRankSum), imbcoef=as.numeric(d12$InbreedingCoeff), group=factor(d12$FILTER), DP=d12$DP/NIND, ReadPosRankSum=as.numeric(d12$ReadPosRankSum))
