png(paste0(outD,"ReadPosRankSum_by_VQSRTranche.png"), width=1200, height=800, type="cairo")

p1 <- ggplot(aes(x = ReadPosRankSum), data=newdata1) + geom_histogram(aes(fill=group)) +xlab("ReadPosRankSum") + ylab("Number") + ggtitle("ReadPosRankSum by VQSRTranche") 

